import React, {useState, useEffect} from 'react'; 



function ToDo(props){
    const [currentTodo, updateList] = useState({newItem:"", list:[]});

    const updateCurrentList = (e) => {
        e.preventDefault(); 
        let currentList = [...currentTodo];
        updateList(currentState => [...currentList, currentTodo]); 
    }
    const decreaseCount = () => {
        if(currentTodo != 0) {
            updateList( currentTodo); 
        }

    }

    useEffect(()=> {
        console.log("welcome to your todo app")
    }, []);

    return(

        <div className ="container">
        <h1> Tests with the current state {currentTodo.newItem} </h1>
        <form> 
        <input className="formInput" type="text" placeholder="Enter your todo Item"
         value={currentTodo} onChange={e => updateList(e.target.value)} />
        <button onClick={updateCurrentList} > Submit </button>
        </form> 
        <ul>
            {currentTodo.list}
        </ul>
        </div>
    ); 

}


export default ToDo;
